var decodeMorse = function (morseCode) {
  if (!morseCode) return '';
  // ToDo: Accept dots, dashes and spaces, return human-readable message
  var r = morseCode.trim().split(' ');
  var res = r.map(function (word) {
    return word == "#" ? " " : MORSE_CODE[word];
  });
  return res.join("");
}

